package _02_loop_variables._4_99_bottles.bottles;

import javax.swing.JOptionPane;

public class bottles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int bottle = 99; 
		for(int i = 0; i<= bottle; bottle--) {
		System.out.println(bottle + " bottles of beer on the wall." + bottle + "bottles of beer. Take one down, pass it around " + (bottle - 1) + " bottles of beer on the wall");
		}
		
				
				
	}

}
